<?php
function mamak_cpt()
{
  // Client Post Type
  register_post_type('client', [
    'capability_type' => 'client',
    'map_meta_cap' => true,
    'supports'  => [
      'title',
      'page-attributes'
    ],
    'rewrite' => [
      'slug' => 'clients'
    ],
    'public' => true,
    'show_in_rest' => true,
    'labels' => [
      'name' => 'Clients',
      'add_new' => 'Add New Client',
      'add_new_item' => 'Add New Client',
      'edit_item' => 'Edit Client',
      'all_items' => 'All Clients',
      'singular_name' => 'Client'
    ],
    'menu_icon' => 'data:image/svg+xml;base64,',
  ]);


  // Service Post Type
  register_post_type('service', [
    'capability_type' => 'service',
    'map_meta_cap' => true,
    'supports'  => [
      'title',
      'page-attributes',
    ],
    'rewrite' => [
      'slug' => 'services'
